snippets.@ff:
@font-face {
	font-family: '${1:FontName}'; /* IE9 */
	src: url('${2:FileName}.eot');
	src: url('${2:FileName}.eot?#iefix') format('embedded-opentype'), /* IE6-IE8 */
		 url('${2:FileName}.woff') format('woff'), /* Chrome,Firefox */
		 url('${2:FileName}.ttf') format('truetype'), /* Chrome,Firefox,Opera,Safari,Android, iOS 4.2+ */
		 url('${2:FileName}.svg#${1:FontName}') format('svg'); /* iOS 4.1- */
}

snippets.placeholder:
/* Firefox 18- */
input:-moz-placeholder, textarea:-moz-placeholder {
	color: ${1:darkGray};
}
/* Firefox 19+ */
input::-moz-placeholder, textarea::-moz-placeholder {
	color: ${1:darkGray};
}
/* IE10+ */
input:-ms-input-placeholder, textarea:-ms-input-placeholder {
	color: ${1:darkGray};
}
input::-webkit-input-placeholder, textarea::-webkit-input-placeholder {
	color: ${1:darkGray};
}

snippets.placeholderfocus:
/* Firefox 18- */
input:focus:-moz-placeholder, textarea:focus:-moz-placeholder {
	color: ${1:darkGray};
}
/* Firefox 19+ */
input:focus::-moz-placeholder, textarea:focus::-moz-placeholder {
	color: ${1:darkGray};
}
/* IE10+ */
input:focus:-ms-input-placeholder, textarea:focus:-ms-input-placeholder {
	color: ${1:darkGray};
}
input:focus::-webkit-input-placeholder, textarea:focus::-webkit-input-placeholder {
	color: ${1:darkGray};
}

snippets.selection:
::-moz-selection{
	background: #${1:b3d4fc};
	text-shadow: none;
}
::selection{
	background: #${1:b3d4fc};
	text-shadow: none;
}

snippets.bgi+:
@media only screen and (-o-min-device-pixel-ratio: 2/1), /* Opera */
       only screen and (min--moz-device-pixel-ratio: 2), /* Firefox 16 之前 */
       only screen and (-webkit-min-device-pixel-ratio: 2), /* WebKit */
       only screen and (min-resolution: 192dpi), /* 不支持dppx的浏览器 */
       only screen and (min-resolution: 2dppx) /* 标准 */
{
	${1:.selector}{
		background-image:url(${2});/* Retina */
		background-size: ${3:图片宽度÷2}px ${4:图片高度÷2}px;
	}
}
